import{WalletConnectModalSign}from"@walletconnect/modal-sign-html";const connectButton=document.getElementById("connect-button"),projectId=import.meta.env.VITE_PROJECT_ID;if(console.log(projectId),!projectId)throw new Error("You need to provide VITE_PROJECT_ID env variable");export const web3Modal=new WalletConnectModalSign({projectId,metadata:{name:"My Dapp",description:"My Dapp description",url:"https://my-dapp.com",icons:["https://my-dapp.com/logo.png"]}});async function onConnect(){try{connectButton.disabled=!0;const n=await web3Modal.connect({requiredNamespaces:{eip155:{methods:["eth_sendTransaction","personal_sign"],chains:["eip155:1"],events:["chainChanged","accountsChanged"]}}});console.info(n)}catch(n){console.error(n)}finally{connectButton.disabled=!1}}connectButton.addEventListener("click",onConnect);